  function getBwah($url, $type){
    $urlArr = array();
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    $result = curl_exec($ch);
    preg_match_all("|<a.*?href=\"(.*?)\"|", $result, $rw);
    $lk = $rw[1];
    foreach($lk as $lnk){
      array_push($urlArr, $lnk);
    }
    curl_close($ch);
    foreach($urlArr as $val){
      $mes = "$url$val";
      if(preg_match("#$type#is", $mes)){
        echo $mes."\r\n";
      }
    }
  }
